package LoopProgram;

public class forLoop {
    static int downfall = 1;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int downfall = 1;
    for (int counter = 10; counter >= downfall; --counter) {
		System.out.println("Countdown begins: " +counter);
	}
	}	
    
}
